
public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=13;
		
		/*
		 * while (i<=10)
		 * 
		 * { System.out.println(i); 
		 * i++; 
		 * System.out.println(i);
		 * 
		 *  }
		 * 
		 * System.out.println("print");
		 */
	
	do {
		i++;
		System.out.println(i);
		
	}	
		
	while (i<=10);
	{
	System.out.println(i);
	}
	System.out.println("after loop");
	
	}

}
